INSERT INTO `news` VALUES ('60', '东方撒', '<p>\r\n	<span style=\"color: rgb(0, 0, 255);\"><span style=\"font-size: 20px;\">发的撒上的放松法第十发的撒<img alt=\"sad\" height=\"20\" src=\"http://localhost:8080/news3.0/ckeditor/plugins/smiley/images/sad_smile.gif\" title=\"sad\" width=\"20\" /></span></span></p>\r\n<hr />\r\n<ol>\r\n	<li>\r\n		的撒</li>\r\n	<li>\r\n		发的撒</li>\r\n	<li>\r\n		的撒\r\n		<p>\r\n			<img alt=\"\" height=\"44\" src=\"C:\\Users\\admin\\Pictures\\p.jpg\" style=\"width: 157px; height: 77px;\" width=\"150\" /></p>\r\n	</li>\r\n</ol>\r\n<p>\r\n	&nbsp;</p>\r\n', '2011-07-10', '东方撒', '7');
INSERT INTO `news` VALUES ('61', '大师', '<p>\r\n	的撒 所得税但是</p>\r\n', '2011-07-11', '大法师', '7');
